// authSlice.js
const authSlice = createSlice({
  name: 'auth',
  initialState: {
    token: null,
    user: null, // Stocke ici les infos de l'utilisateur
  },
  reducers: {
    login: (state, action) => {
      state.token = action.payload.token;
      state.user = action.payload.user;
    },
    logout: (state) => {
      state.token = null;
      state.user = null;
    },
    updateProfile: (state, action) => {
      state.user = { ...state.user, ...action.payload }; // Met à jour les informations du profil
    }
  },
});

export const { login, logout, updateProfile } = authSlice.actions;
export default authSlice.reducer;
