import { createSlice } from "@reduxjs/toolkit";

const authSlice = createSlice({
  name: "auth",
  initialState: {
    username: null, // Ou firstName selon ce que tu veux
    isLoggedIn: false,
  },
  reducers: {
    login: (state, action) => {
      state.username = action.payload.firstName; // Ou username
      state.isLoggedIn = true;
    },
    logout: (state) => {
      state.username = null;
      state.isLoggedIn = false;
    },
  },
});

export const { login, logout } = authSlice.actions;
export default authSlice.reducer;
