// src/pages/User/user.js
import React, { useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { updateProfile } from "../../redux/Slices/authSlice"; // Action pour mettre à jour le profil
import "./user.css";

function User() {
  const dispatch = useDispatch();
  const { firstName, lastName, userName } = useSelector((state) => state.auth);

  // Les états locaux pour les valeurs du formulaire
  const [newFirstName, setNewFirstName] = useState(firstName || "");
  const [newLastName, setNewLastName] = useState(lastName || "");
  const [newUserName, setNewUserName] = useState(userName || "");

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Appeler l'API pour mettre à jour le profil
      const response = await fetch("http://localhost:3001/api/v1/user/profile", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`, // inclure le token
        },
        body: JSON.stringify({
          firstName: newFirstName,
          lastName: newLastName,
          userName: newUserName,
        }),
      });

      if (!response.ok) {
        throw new Error("Erreur lors de la mise à jour du profil");
      }

      const updatedData = await response.json();

      // Mettre à jour le state Redux avec les nouvelles infos
      dispatch(updateProfile(updatedData.body));
    } catch (error) {
      console.error("Erreur de mise à jour du profil :", error);
    }
  };

  return (
    <main className="main bg-dark">
      <div className="header">
        <h1>Modifier le Profil</h1>
        <form onSubmit={handleSubmit}>
          <div>
            <label>Prénom:</label>
            <input
              type="text"
              value={newFirstName}
              onChange={(e) => setNewFirstName(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Nom:</label>
            <input
              type="text"
              value={newLastName}
              onChange={(e) => setNewLastName(e.target.value)}
              required
            />
          </div>
          <div>
            <label>Nom d'utilisateur:</label>
            <input
              type="text"
              value={newUserName}
              onChange={(e) => setNewUserName(e.target.value)}
              required
            />
          </div>
          <button type="submit">Enregistrer les modifications</button>
        </form>
      </div>
    </main>
  );
}

export default User;
