import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { setUser, updateName } from "../../redux/Slices/userSlice";
import { useNavigate } from "react-router-dom";

function User() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { firstName, lastName } = useSelector((state) => state.user);
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);

  const [newFirstName, setNewFirstName] = useState(firstName);
  const [newLastName, setNewLastName] = useState(lastName);
  const [isEditing, setIsEditing] = useState(false);

  // Fonction pour récupérer les données utilisateur via l'API
  async function getUser() {
    const token = localStorage.getItem("token");
    if (!token) {
      console.error("No token found, user is not authenticated.");
      return;
    }

    try {
      const response = await fetch("http://localhost:3001/api/v1/user/profile", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
      });

      if (!response.ok) {
        throw new Error("Failed to fetch user data");
      }

      const data = await response.json();
      return data.body;
    } catch (error) {
      console.error("Error fetching user data:", error);
      throw error;
    }
  }

  // useEffect pour récupérer les données au chargement de la page
  useEffect(() => {
    if (!isLoggedIn) {
      navigate("/sign-in"); // Redirige vers la page de connexion si pas connecté
    } else {
      getUser()
        .then((userData) => {
          dispatch(setUser(userData)); // Stocke les données utilisateur dans Redux
          setNewFirstName(userData.firstName); // Pré-remplit les champs
          setNewLastName(userData.lastName);
        })
        .catch((error) => console.error("Error:", error));
    }
  }, [isLoggedIn, navigate, dispatch]);

  const handleFormSubmit = (e) => {
    e.preventDefault();
    dispatch(updateName({ firstName: newFirstName, lastName: newLastName }));
    setIsEditing(false);
  };

  return (
    <main className="main bg-dark">
      <div className="header">
        <h1>
          Welcome back
          <br />
          {isEditing ? (
            <form onSubmit={handleFormSubmit}>
              <input
                type="text"
                value={newFirstName}
                onChange={(e) => setNewFirstName(e.target.value)}
                required
              />
              <input
                type="text"
                value={newLastName}
                onChange={(e) => setNewLastName(e.target.value)}
                required
              />
              <button type="submit">Save</button>
              <button type="button" onClick={() => setIsEditing(false)}>
                Cancel
              </button>
            </form>
          ) : (
            <>
              {firstName} {lastName}!
              <button className="edit-button" onClick={() => setIsEditing(true)}>
                Edit Name
              </button>
            </>
          )}
        </h1>
      </div>
      {/* Le reste du contenu */}
    </main>
  );
}

export default User;
