// src/pages/Sign-in/sign-in.js
import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { login } from "../../redux//Slices/authSlice"; // Assurez-vous d'importer l'action login
import { useNavigate } from "react-router-dom";

function SignIn() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3001/api/v1/user/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        throw new Error("Identifiants invalides");
      }

      const data = await response.json();

      // Supposons que tu reçois le firstName dans la réponse
      dispatch(login({ firstName: data.body.firstName })); // Mettre à jour le state Redux avec le firstName
      localStorage.setItem("token", data.body.token); // Stocker le token
      localStorage.setItem("firstName", data.body.firstName); // Stocker le firstName
      navigate("/user"); // Redirige vers la page utilisateur
    } catch (error) {
      console.error("Erreur de connexion:", error);
    }
  };

  return (
    <form onSubmit={handleLogin}>
      <input
        type="text"
        value={username}
        onChange={(e) => setUsername(e.target.value)}
        placeholder="Nom d'utilisateur"
        required
      />
      <input
        type="password"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
        placeholder="Mot de passe"
        required
      />
      <button type="submit">Se connecter</button>
    </form>
  );
}

export default SignIn;
