import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { updateName } from "../../redux/Slices/userSlice";
import "./edit.css";

async function EditUser({ userName, firstName, lastName, onCancel }) {
  const dispatch = useDispatch();
  const [newUserName, setNewUserName] = useState(userName);
  const [newFirstName, setNewFirstName] = useState(firstName);
  const [newLastName, setNewLastName] = useState(lastName);

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    dispatch(updateName({ userName: newUserName, firstName: newFirstName, lastName: newLastName }));
    onCancel();

    const token = localStorage.getItem("token");
    if (!token) {
      console.error("No token found, user is not authenticated.");
      return;
    }

    try {
      const response = await fetch("http://localhost:3001/api/v1/user/profile", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ userName }),
      });

      if (!response.ok) {
        throw new Error("Failed to fetch user data");
      }

      const data = await response.json();
      return data.body;
    } catch (error) {
      console.error("Error fetching user data:", error);
      throw error;
    }
  }



  return (
    <form className="formEdit" onSubmit={handleFormSubmit}>
      <label className="textEdit" htmlFor="userName">
        User name:
      </label>
      <input className="inputEdit" type="text" value={newUserName} onChange={(e) => setNewUserName(e.target.value)} required />
      <label className="textEdit" htmlFor="userName">
        First name:
      </label>
      <input className="inputEdit" type="text" value={newFirstName} onChange={(e) => setNewFirstName(e.target.value)} required />
      <label className="textEdit" htmlFor="userName">
        Last name:
      </label>
      <input className="inputEdit" type="text" value={newLastName} onChange={(e) => setNewLastName(e.target.value)} required />
      <div className="button">
        <button className="buttonEdit" type="submit">
          Save
        </button>
        <button className="buttonEdit" type="button" onClick={onCancel}>
          Cancel
        </button>
      </div>
    </form>
  );
}

export default EditUser;
