import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { updateName, setUser } from "../../redux/Slices/userSlice"; // Import de l'action setUser
import { useNavigate } from "react-router-dom";

function User() {
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { firstName, lastName } = useSelector((state) => state.user);
  const isLoggedIn = useSelector((state) => state.auth.isLoggedIn);
  const [isEditing, setIsEditing] = useState(false);
  const [newFirstName, setNewFirstName] = useState(firstName);
  const [newLastName, setNewLastName] = useState(lastName);

  // Fonction pour récupérer les informations de l'utilisateur
  async function getUser() {
    const token = localStorage.getItem("token"); // Récupération du token
    try {
      const response = await fetch(`http://localhost:3001/api/v1/user/profile`, {
        method: "POST",
        headers: { Authorization: `Bearer ${token}` },
      });
      const data = await response.json();
      dispatch(setUser(data.body)); // Mise à jour du store Redux
    } catch (error) {
      console.error("Erreur lors de la récupération des informations utilisateur", error);
    }
  }

  useEffect(() => {
    if (!isLoggedIn) {
      navigate("/signin"); // Redirige si l'utilisateur n'est pas connecté
    } else {
      getUser(); // Appelle la fonction pour récupérer les infos utilisateur
    }
  }, [isLoggedIn, dispatch, navigate]);

  const handleEditClick = () => {
    setIsEditing(!isEditing);
  };

  const handleFormSubmit = (e) => {
    e.preventDefault();
    dispatch(updateName({ firstName: newFirstName, lastName: newLastName })); // Mets à jour les infos dans Redux
    setIsEditing(false); // Ferme le mode édition après soumission
  };

  return (
    <main className="main bg-dark">
      <div className="header">
        <h1>
          Welcome back
          <br />
          {isEditing ? (
            <form onSubmit={handleFormSubmit}>
              <input
                type="text"
                value={newFirstName}
                onChange={(e) => setNewFirstName(e.target.value)}
                required
              />
              <input
                type="text"
                value={newLastName}
                onChange={(e) => setNewLastName(e.target.value)}
                required
              />
              <button type="submit">Save</button>
              <button type="button" onClick={handleEditClick}>
                Cancel
              </button>
            </form>
          ) : (
            <>
              {firstName} {lastName}!
              <button className="edit-button" onClick={handleEditClick}>
                Edit Name
              </button>
            </>
          )}
        </h1>
      </div>
    </main>
  );
}

export default User;
