import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { updateName } from "../../redux/Slices/userSlice"; // Ajustez l'importation selon votre structure de fichiers

function EditUser({ userName, firstName, lastName, onCancel }) {
  const dispatch = useDispatch();
  const [newUserName, setNewUserName] = useState(userName);
  const [newFirstName, setNewFirstName] = useState(firstName);
  const [newLastName, setNewLastName] = useState(lastName);

  const handleFormSubmit = async (e) => {
    e.preventDefault();

    // Mettez à jour le nom dans Redux
    dispatch(updateName({ userName: newUserName, firstName: newFirstName, lastName: newLastName }));

    const token = localStorage.getItem("token");
    if (!token) {
      console.error("No token found, user is not authenticated.");
      return;
    }

    try {
      const response = await fetch("http://localhost:3001/api/v1/user/profile", {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${token}`,
        },
        body: JSON.stringify({ userName: newUserName, firstName: newFirstName, lastName: newLastName }),
      });

      if (!response.ok) {
        throw new Error("Failed to fetch user data");
      }

      const data = await response.json();
      console.log(data.body); // Traitez les données reçues si nécessaire
      onCancel(); // Fermez le formulaire après la soumission
    } catch (error) {
      console.error("Error fetching user data:", error);
      // Gérez l'erreur si nécessaire (par exemple, afficher un message d'erreur)
    }
  };

  return (
    <form onSubmit={handleFormSubmit}>
      <label>
        User Name:
        <input 
          type="text" 
          value={newUserName} 
          onChange={(e) => setNewUserName(e.target.value)} 
        />
      </label>
      <label>
        First Name:
        <input 
          type="text" 
          value={newFirstName} 
          onChange={(e) => setNewFirstName(e.target.value)} 
        />
      </label>
      <label>
        Last Name:
        <input 
          type="text" 
          value={newLastName} 
          onChange={(e) => setNewLastName(e.target.value)} 
        />
      </label>
      <button type="submit">Save</button>
      <button type="button" onClick={onCancel}>Cancel</button>
    </form>
  );
}

export default EditUser;
