import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { login } from "../../redux/action";
import { useNavigate } from "react-router-dom";

function SignIn() {
  const dispatch = useDispatch();
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch("http://localhost:3001/api/v1/user/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });

      if (!response.ok) {
        throw new Error("Identifiants invalides");
      }

      const data = await response.json();

      // Log de la réponse pour voir ce qui est retourné par l'API
      console.log("Données reçues de l'API:", data);

      // Vérifier si le token est présent dans data
      if (data.token) {
        // Stocker le token dans le localStorage
        localStorage.setItem("token", data.token);
        console.log("Token sauvegardé dans localStorage:", data.token);

        // Rediriger l'utilisateur vers la page /user
        navigate("/user");
      } else {
        console.error("Aucun token dans la réponse API");
      }
    } catch (error) {
      console.error("Erreur de connexion:", error);
    }
  };

  return (
    <main className="main bg-dark">
      <section className="sign-in-content">
        <i className="fa fa-user-circle sign-in-icon"></i>
        <h1>Sign In</h1>
        <form onSubmit={handleLogin}>
          <div className="input-wrapper">
            <label htmlFor="username">Username</label>
            <input type="text" id="username" value={email} onChange={(e) => setEmail(e.target.value)} />
          </div>
          <div className="input-wrapper">
            <label htmlFor="password">Password</label>
            <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} />
          </div>
          <div className="input-remember">
            <input type="checkbox" id="remember-me" />
            <label htmlFor="remember-me">Remember me</label>
          </div>

          <button type="submit" className="sign-in-button">
            Sign In
          </button>
        </form>
      </section>
    </main>
  );
}

export default SignIn;
