import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  isLoggedIn: false,
  token: null,
  errorMessage: "", // État pour le message d'erreur
};

const authSlice = createSlice({
  name: "auth",
  initialState,
  reducers: {
    login(state, action) {
      state.isLoggedIn = true;
      state.token = action.payload.body.token;
      state.errorMessage = ""; // Réinitialiser le message d'erreur lors de la connexion réussie
    },
    logout(state) {
      state.isLoggedIn = false;
      state.token = null;
      state.errorMessage = ""; // Réinitialiser lors de la déconnexion
    },
    setError(state, action) {
      state.errorMessage = action.payload; // Mettre à jour le message d'erreur
    },
  },
});

export const { login, logout, setError } = authSlice.actions;
export default authSlice.reducer;
